import { Component ,EventEmitter,Output } from '@angular/core';




@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'angularapp';
  ImagePath: string;
  name='';
  foodName:newFood[] =[];
  image ='';
  calories=0;

  count:number;



  getValues(val:any)
  {
    this.foodName.push(val);
  }

  Oclick()
  {
    console.log(this.count);

  }
  enteredSearchvalue:string = '';

  @Output()
  searchTextChanged: EventEmitter<string> = new EventEmitter<string>();

  onSearchTextChanged(){
    this.searchTextChanged.emit(this.enteredSearchvalue);
  }
  searchText:string = '';
  Onkey(searchValue: string){
    this.searchText = searchValue;
    console.log(this.searchText);
  }

}
interface newFood {
  name: string;
  calories: number;
  image: string;
}

<h2>Food List</h2>

<input type="text" [(ngModel)]="searchText" placeholder="Search foods by name" />

<div class="food-item" *ngFor="let food of foods | foodFilter: searchText">
  <img [src]="food.imageSrc" alt="Food Image">
  <p>Name: {{ food.name }}</p>
  <p>Calories: {{ food.calories }}</p>
  <button (click)="addToTodaysFoods(food)">Add to Today's List</button>
</div>
